    constructor(props) {
        super(props)

        this.state = {
            count: 0
        }

        this.increment = this.increment.bind(this);
        this.decrement = this.decrement.bind(this);
        this.reset = this.reset.bind(this);
        // this.handleClick = this.handleClick.bind(this);
    }

    increment() {
        this.setState(state => ({
            count: state.count + 1
        }))
    }
    
    
    decrement() {
        this.setState(state => ({
            count: state.count - 1
        }))
    }


    reset() {
        this.setState({
            count: 0
        })
    }
